mapper.select:
SELECT member_id, member_pw, member_type, member_name, member_email, member_level, member_photo
FROM MEMBER
WHERE MEMBER_ID=#{memberId}

		SELECT LESSON_NO, MYCLASS_TOTALCNT, MYCLASS_REMAINCNT
		FROM myclass
		JOIN lesson USING(lesson_no)


					WHERE member_id=#{memberId}


                	WHERE tutor_id=#{memberId}

SELECT member_photo, member_id FROM MEMBER JOIN lesson ON(member_id = tutor_id)
WHERE lesson_no IN (select lesson_no from myclass where member_id = #{memberId})
WHERE tutor_id = #{memberId}
